INSERT INTO `blog_message` VALUES ('82', 'feng', '&lt;table align=&quot;center&quot; width=&quot;100%&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td width=&quot;299&quot; style=&quot;font-family: Verdana, Helvetica, Arial; word-break: break-all; line-height: 1.5;&quot;&gt;&lt;br/&gt;&lt;/td&gt;&lt;td style=&quot;font-family: Verdana, Helvetica, Arial; word-break: break-all; line-height: 1.5;&quot;&gt;&lt;br/&gt;&lt;/td&gt;&lt;td width=&quot;296&quot; style=&quot;font-family: Verdana, Helvetica, Arial; word-break: break-all; line-height: 1.5;&quot;&gt;&lt;br/&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;a class=&quot;menu&quot; href=&quot;http://www.cnblogs.com/&quot; style=&quot;color: rgb(111, 188, 76);&quot;&gt;博客园&lt;/a&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;a class=&quot;menu&quot; href=&quot;http://www.cnblogs.com/bestsaler/&quot; style=&quot;color: rgb(111, 188, 76);&quot;&gt;首页&lt;/a&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;a href=&quot;http://q.cnblogs.com/&quot; class=&quot;menu&quot; target=&quot;_blank&quot; style=&quot;color: rgb(111, 188, 76);&quot;&gt;博问&lt;/a&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;a href=&quot;http://home.cnblogs.com/ing/&quot; class=&quot;menu&quot; target=&quot;_blank&quot; style=&quot;color: rgb(111, 188, 76);&quot;&gt;闪存&lt;/a&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;a class=&quot;menu&quot; href=&quot;https://i.cnblogs.com/EditPosts.aspx?opt=1&quot; style=&quot;color: rgb(111, 188, 76);&quot;&gt;新随笔&lt;/a&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;a class=&quot;menu&quot; href=&quot;http://www.cnblogs.com/bestsaler/rss&quot; style=&quot;color: rgb(111, 188, 76);&quot;&gt;订阅&lt;/a&gt;&amp;nbsp;&lt;a href=&quot;http://www.cnblogs.com/bestsaler/rss&quot; style=&quot;color: rgb(111, 188, 76);&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20170420/1492679245601928.gif&quot; alt=&quot;订阅&quot;/&gt;&lt;/a&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;a class=&quot;menu&quot; href=&quot;https://i.cnblogs.com/&quot; style=&quot;color: rgb(111, 188, 76);&quot;&gt;管理&lt;/a&gt;&lt;/p&gt;&lt;p&gt;posts - 395,&amp;nbsp; comments - 4,&amp;nbsp; trackbacks - 0&lt;/p&gt;&lt;p&gt;&lt;a class=&quot;postTitle2&quot; href=&quot;http://www.cnblogs.com/bestsaler/archive/2010/04/08/1835508.html&quot; style=&quot;color: rgb(100, 100, 100); text-decoration: none;&quot;&gt;jquery ajax POST 例子详解&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin: 10px auto;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; function test(){&lt;br/&gt;&amp;nbsp;&amp;nbsp; $.ajax({&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //提交数据的类型 POST GET&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; type:&amp;quot;POST&amp;quot;,&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //提交的网址&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; url:&lt;a href=&quot;http://www.cnblogs.com/wangdetian168/admin/%22testLogin.aspx&quot; target=&quot;_blank&quot; style=&quot;color: rgb(111, 188, 76);&quot;&gt;&amp;quot;testLogin.aspx&lt;/a&gt;&amp;quot;,&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //提交的数据&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; data:{Name:&amp;quot;sanmao&amp;quot;,Password:&amp;quot;sanmaoword&amp;quot;},&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //返回数据的格式&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; datatype: &amp;quot;html&amp;quot;,//&amp;quot;xml&amp;quot;, &amp;quot;html&amp;quot;, &amp;quot;script&amp;quot;, &amp;quot;json&amp;quot;, &amp;quot;jsonp&amp;quot;, &amp;quot;text&amp;quot;.&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //在请求之前调用的函数&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; beforeSend:function(){$(&amp;quot;#msg&amp;quot;).html(&amp;quot;logining&amp;quot;);},&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //成功返回之后调用的函数&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; success:function(data){&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $(&amp;quot;#msg&amp;quot;).html(decodeURI(data));&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; }&amp;nbsp;&amp;nbsp; ,&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //调用执行后调用的函数&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; complete: function(XMLHttpRequest, textStatus){&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; alert(XMLHttpRequest.responseText);&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; alert(textStatus);&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //HideLoading();&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; },&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //调用出错执行的函数&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; error: function(){&lt;br/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //请求出错处理&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '/Uploads/top/100.jpg', '2017-04-20 17:07:32', '81');
